  journalistsnumber = start
# Permet de réutiliser la variable "journalistsnumber" dans chaque methode
  array_length (journalistsnumber)
# c'est la méthode 1
  array_min (journalistsnumber)
# c'est la méthode 2
  arrayfivecharactersnumber (journalistsnumber)
# c'est la méthode 3
  arraystartswithcapital (journalistsnumber)
# c'est la méthode 4
  arraysortedalphabet (journalistsnumber)
# c'est la méthode 5
  arraysortedlength (journalistsnumber)
  # c'est la méthode 6
  arrayepenserposition (journalistsnumber)
  # c'est la méthode 7
  arrayhandlerepartition (journalistsnumber)
  # c'est la méthode 8
 end

